mapper.select:
select * from ESERVICE_JD.dbo.USERS a
    left join ESERVICE_ADM.dbo.USER_ENTITY c on a.USER_ID = c.USERNAME
    left join  ESERVICE_JD.dbo.USER_DEPARTMENT b on c.ID = b.USER_ID


        AND b.DEP_ID = #{depId}


        AND a.IC_ID = #{icId}

select b.ID userId, c.DEP_ID, a.IC_ID from ESERVICE_JD.dbo.USERS a left join ESERVICE_ADM.dbo.USER_ENTITY b on a.USER_ID = b.USERNAME left join ESERVICE_JD.dbo.USER_DEPARTMENT c on b.ID = c.USER_ID left join ESERVICE_ADM.dbo.USER_ROLE d on b.ID = d.USER_ID left join ESERVICE_ADM.dbo.ROLE e on d.ROLE_ID = e.ROLE_ID
e.DIV_ROLE_ID = 'IC_PERSONNEL'
and a.IC_ID = #{icId}
and c.DEP_ID = #{depId}
