FROM ESERVICE.DBO.TRANS T JOIN ESERVICE.DBO.TRANS_DNS F ON T.TRANS_NUM = F.TRANS_NUM WHERE convert(varchar(10), T.CREATE_DATE, 120) >= convert(varchar(10), #{transVo.startDate}, 120) AND convert(varchar(10), T.CREATE_DATE, 120) <= convert(varchar(10), #{transVo.endDate}, 120)
