AND T.RFE_ID = #{vo.rfeId, jdbcType=VARCHAR}
AND T.REQUEST_DATE > #{vo.startDate, jdbcType=DATE}
AND T.REQUEST_DATE < #{vo.endDate, jdbcType=DATE}
order by T.REQUEST_DATE desc

