mapper.select:
SELECT TOP 1 CASE_ID
FROM [ESERVICE].[dbo].[TRANS_MEDICAL_TREATMENT_CLAIM]
WHERE   TRANS_NUM=#{transNum}

SELECT [SEQ_ID] ,[CASE_ID] ,[TYPE] ,[CREATE_DATE] ,[NC_STATUS] ,[STATUS_DATE] ,[MSG] ,COALESCE(CASE_STATUS,' ') AS CASE_STATUS ,COALESCE((SELECT a1.PARAMETER_NAME FROM ESERVICE_ADM.DBO.PARAMETER a1 ,ESERVICE_ADM.DBO.PARAMETER_CATEGORY b1 WHERE a1.PARAMETER_CATEGORY_ID = b1.PARAMETER_CATEGORY_ID AND a1.STATUS = b1.STATUS AND b1.STATUS = 1 AND b1.CATEGORY_CODE = 'MEDICAL_INTERFACE_STATUS' AND a1.PARAMETER_VALUE =CASE_STATUS),' ') AS CASE_MESSAGE FROM ESERVICE.dbo.[NOTIFY_OF_NEW_CASE_MEDICAL] WHERE 1=1
AND CASE_ID = #{notifyVo.caseId}
AND NC_STATUS = #{unionCourseVo.ncStatus}
AND convert(varchar(10), STATUS_DATE, 120) >= convert(varchar(10), #{notifyVo.createDate}, 120)
AND convert(varchar(10), STATUS_DATE, 120) <= convert(varchar(10), #{notifyVo.completeDate}, 120)
ORDER BY CASE_STATUS DESC

